(function() {	tinymce.create('tinymce.plugins.EmbedGalleryImage', {		init : function(ed, url) {						ed.addButton('embed_video_link', {				title: 'Insert Thumbnail Image',				image: 'files/jscripts/tiny_mce/plugins/embed_video_link/images/tv-icon.png',				onclick : function(){					 ed.windowManager.open({						url: url + "/select_video_link.htm",						width: 730,						height: 600,						inline: 1					 },{						title: "Insert Video Link",						plugin_url : url					 });				},				'class' : 'bold' // Use the bold icon from the theme			});		}	});		tinymce.PluginManager.add('embed_video_link', tinymce.plugins.EmbedGalleryImage);})();