<?phpfunction send_form_email($recipients, $form_name, $fields){		if(!is_array($recipients)){		$recipients = array($recipients);	}		$lines = array();		foreach($fields as $name => $value){		$lines[] = wordwrap(ucwords($name) . ": " . $value);	}		$headers = "";	$subject = "A $form_name Form has been submitted at " . SITE_NAME;	$body = implode("\r\n", $lines);		foreach($recipients as $recipient){		mail($recipient, $subject, $body, $headers);	}}